INSERT INTO `products` (`id`, `name`, `category_id`, `availability`, `description`, `characteristics`, `is_new`, `is_recommended`, `status`) VALUES
(1, 'Интерактивная игровая стойка Leapfrog', 12, 1, 'На этой «умной» площадке от Leap Frog ребенок сможет учиться в любом положении – и стоя, и сидя, и ползая вокруг нее. \r\nКогда малыш осваивает мир, сидя на полу, игровая площадка предлагает ему прекрасные познавательные возможности: \r\n«Поющая радуга» знакомит с цветами и формами. \r\nЛоскутки ткани «прыгай и учи» развивают ощущение неровной, пушистой и гладкой поверхности. \r\nНижний желобок для мяча «Ба-бах!» развивает моторные навыки. \r\nЕсли ребенок уже начал самостоятельно стоять, передвигаться и готов узнать больше, игровая площадка начнет новые этапы обучения с еще большим количеством действий, песен и веселья: \r\nАлфавит «Двигай и пой» поможет выучить песенку про алфавит. \r\nБуквы « Нажми и учи» обучают алфавиту. \r\n«Аэроплан летающих рифм» обучает рифмам и мелодиям. \r\n«Числовая рок-клавиатура» обучает счету от 1 до 5 при помощи песенки в ритме рок-н-ролла. \r\n«Выскакивающие противоположности» – вводят понятия слов, противоположных по значению. \r\nВ музыкальном режиме игровая площадка будет развивать у ребенка способности к распознаванию звуков и побуждает его самостоятельно сочинять мелодии. способствуют развитию у ребенка мыслительных навыков и творческой активности.', '', 0, 0, 1),
(2, 'Столик "Пианино" Fisher price', 12, 1, 'Игровой детский столик предназначен для детей от 6 месяцев и старше. \r\nПианино от фишер прайс - это настоящий инструмент для самых маленьких музыкантов. Крупные цветные клавиши понравятся ребенку. \r\nБольшая нотная книга с перекидными листами изменяет режим игры : можно просто играть на пианино красивые мелодии, можно изучать звучание нот и их названия, можно учить цвета и цифры. \r\nВеселое солнышко двигает руками в такт музыке и улыбается малышу. \r\nРебенок может играть со столиком сидя перед роялем или стоя, опираясь на него.', NULL, 0, 0, 1),
(3, 'Столик музыкальный Vtech', 12, 1, 'Яркий столик наполнен деталями и игровыми элементами. Множество вариантов игры: \r\nДетский телефон трубкой и большими кнопками научит ребенка считать до 10. Звонит как настоящий телефон. Воспроизводит голоса животных и называет их (собачка, котик, лягушка, овечка, корова, свинка). \r\nМузыкальные клавиши познакомят малыша не только с основными цветами, но и с нотами. \r\nПерекидная книжка с большой кнопкой изменяет режим игры столика, а еще весело пропоет алфавит вместе с ребенком. \r\nБольшой компас с поворотной стрелкой поможет маме и малышу разобраться в режиме дня и выучить, когда нужно обедать, в какое время играть, и когда пора ложиться спать. \r\nБольшой вращающийся барабан и перекидные пуговки способствуют развитию мелкой моторики рук и очень нравятся детям. \r\nИграть со столик могут как самые маленькие первооткрыватели: ножки игрушки убираются и игровая панель кладется перед сидящим малышом. Дети, которые учатся стоять и ходить- опираясь на столик малыш может вставать и передвигаться вокруг игрушки.', NULL, 0, 0, 1),
(4, 'Музыкальный развивающий столик с сенсорным барабаном Leap Frog', 12, 1, 'Столик считается одним из лучших в США и Европе так как его яркость и разнообразность развивающей панели очень любимы малышами. Музыкальных композиций в этом столике более 100! Это самый музыкальный столик с очень хорошей озвучкой, в столик встроен сенсорный барабан, ударяя по поверхности которого ребенок увидит мигающие огоньки в виде разноцветных геометрических фигур. Играть можно как в положении сидя, так и стоя. Столик работает в двух режимах, а на его поверхности находится семь различных игровых зон, которые призваны развить основные навыки малыша. \r\nПокрутив тело оранжевой рыбки в виде прозрачного барабанчика, можно услышать шум перекатывающихся маленьких шариков. Открыв пасть дружелюбному бегемоту, малыш увидит свое отражение в безопасном зеркальце, которое спрятано внутри. Также можно нажимать на кнопки, крутить музыкальную шестеренку на гитаре, переворачивать странички книжки, а игровой процесс при этом будет сопровождаться миганием огоньков и забавными музыкальными звуками и веселыми песенками. ', NULL, 0, 0, 1),
(5, 'Музыкальный столик Chicco "Звуки и формы"', 12, 1, 'Столик музыкальный Chicco «Звуки и формы» надолго привлечет внимание малыша! Игрушка многофункциональная с разными игровыми режимами: - Игровой центр: множество игр со смешными звуками животных и звуковыми эффектами; - Электронная клавиатура со светящимися кнопочками и 3-мя игровыми режимами (память на мелодии, музыкальные ноты и записанные мелодии); - Игровая панель с геометрическими формами: если столик перевернуть, он превращается в игровой центр с кубиками, звуками и огоньками.', NULL, 0, 0, 1),
(6, 'Little Tikes развивающий центр "Музыкальный океан"', 12, 1, 'Игрушка Little Tikes развивающий центр "Музыкальный океан" Развивающий игровой центр для малышей в водной тематике. \r\nКомплекс состоит из: \r\n• двигающихся съемно-подвесных панелей \r\n• трех симпотичных морских обитателей – краба \r\n• вращающейся погремушки в форме моллюска и зеркала,которые безусловно привлекут внимание каждого ребенка. \r\nКомплекс отличается множеством звуковых и световых эффектов. Уникальность комплекса в том, что он растет вместе с малошом. Малыш с первых дней жизни до полугода может лежать и следить за морскими обитателями, слышать музыку, смотреть на мигающие огоньки, пытаться дотронуться до игрушек. \r\nДети до года, которые уже научились сидеть - для них в комплексе устанавливается панель перпендикулярно полу, так малышу будет удобнее нажимать на разнообразные кнопочки и видеть эффект от своих действий, т.е. осозновать причинно-следственные связи. \r\nДля малышей, которые уже учатся ходить, панель необходимо установить параллельно полу (центр становится похож на столик), теперь малыш может самостоятельно подходить к нему, нажимать кнопки и снимать подвески. \r\nДанный комплекс способствует развитию мелкой и крупной моторики ребенка, внимания, образного мышления. \r\nКомплекс изготовлен из высококачественной пластмассы.', NULL, 0, 0, 1),
(7, 'Развивающий коврик Tiny love "Лесное озеро"', 3, 1, 'Очаровательный коврик со сказочными персонажами с дугами и множеством увлекательных игрушек, которые будут развлекать и развивать вашего малыша.\r\nОсобенности коврика:\r\nстрекоза, играющая в прятки\r\nудобная для хватания погремушка с бусинами\r\nзеркальце\r\nшуршащие элементы\r\nпрорезыватели для зубов\r\nпогремушки лягушонок и бобер с шуршащим хвостом\r\nсветомузыкальная улитка: 20 минут непрерывной музыки и 10 различных мелодий. Улитку можно как и прикрепить снизу коврика, так и прикрепить на дугу.\r\nдополнительные петли для того, чтобы передвинуть или добавить игрушки\r\nСкользящие кольца позволяют гибкое расположение игрушки, облегчая бесконечные варианты смены положения. Это удобный и компактный коврик, который подходит как для игр дома так и для путешествий.', NULL, 0, 0, 1),
(8, 'Коврик "Пианино" Fisher-Price', 3, 1, 'Развивающий игровой коврик "Пианино", Fisher-Price - идеальное место для отдыха и игр малыша. На коврике изображена красочная сценка со множеством забавных зверюшек, играющих на музыкальных инструментах, которые малыш с интересом будет рассматривать. С одной стороны к коврику прикреплено съемное пластиковое пианино с крупными клавишами, на которые ребенок может нажимать как ручками (в положении сидя), так и ножками (в положении лежа) или играть с ним отдельно. У пианино предусмотрено два режима: для короткой (ноты, короткие песенки) и продолжительной игры (длительные мелодии до 15 минут непрерывного проигрывания), имеется регулятор громкости. \r\nНа съемной дуге подвешены яркие красочные игрушки - зеркальце в виде цветочка, грызунок Гиппопотам, погремушка Слоник, Лягушка с вращающимся валиком, мягкий медальон с тигренком. Малыш может играть сидя или лежа, яркие картинки, привлекательные игрушки и веселые мелодии будут побуждать его к двигательной активности, развивать мелкую моторику, слуховые и зрительные навыки. Коврик компактно складывается и не занимает много места, изготовлен из гипоаллергенных, качественных и совершенно безопасных для малыша материалов.', NULL, 0, 0, 1),
(9, 'Развивающий коврик Tiny love с интерактивной панелью', 3, 1, 'Хит продаж!\r\nВ комплект коврика входят:\r\n- 3 тканевых безопасных погремушки: утенок с бочонком-колокольчиком, жираф, львенок; \r\n- прорезыватель для зубов - 3 разноцветных листочка на кольце держателе; \r\n- пластмассовая погремушка-прорезыватель; \r\n- большое безопасное зеркало в мягкой оправе крепится ремешками на липучках к дуге и коврику, его можно переместить или убрать, диаметр зеркала 25см.\r\n- игровой музыкальный центр «Стукни и играй» («Kick&Play») с 2 режимами работы. \r\nНазвание развивающему комплексу дал музыкальный центр «Стукни и играй» («Kick&Play») с двумя режимами работы. На музыкальной панели центра с одной стороны нарисованы ладошки, а с другой - ступни ног. Если по ним стукнуть, то начинают мигать огоньки и звучат голоса различных животных, а сами ладошки и ступни светятся. В зависимости от положения музыкальной панели: вертикальном (стучим ножкой), или горизонтальном (стучим ручкой), а так же от того какой ручкой или ножкой ударит малыш, звучат разные музыкальные композиции и голоса животных. Так же есть второй режим работы - 15 мин. звучит приятная музыка Моцарта. \r\nРазвивать малышу тактильное восприятие и навыки мелкой моторики помогут 3D-активные фрагменты картинок, размещенные на коврике: здесь есть белочка с шуршащим хвостом, хрустящее солнышко, закрывающее птичку, и воздушный змей, за которым спрятались пчелки, жираф с бархатистой и шелестящей шеей, мышку можно потянуть за хвостик - он растянется, в одной из овечек секрет - пищалка. Игровой коврик «Стукни и играй» имеет трехмерно-пространственную форму с округлыми пересекающимися арками, а бортики коврика можно поднять и закрепить при помощи кнопок. «Эффект арок» помогает расслабиться крохе, обеспечивая визуально безопасное пространство - это напоминает малышу о пребывании в уютном мамином животе. \r\nРазглядывая себя в зеркале, малыш «разговаривает» с отражением, лепечет, осваивает смысловое значение произносимых звуков. Малыш не теряет уверенности в собственной безопасности и при этом развлекается, слушая нежную музыку и разглядывая, висящие над ним веселые красочные игрушки, которые словно приглашают достать себя, потрогать, схватить и подтянуть к себе поближе. Малыш, играя, тренирует кисти рук, их силу и ловкость. Лежа на животике, малыш стремиться поднимать голову и плечи, чтобы видеть игрушки, картинки и зеркало. Он хочет взять игрушку - и переносит вес тела на одну руку. Такая «тренировка» поможет ему быстрее научиться ползать. Все это позволяет стать нашему коврику «особенным» местом для ребенка. \r\nКроме того, игровой коврик «Стукни и играй»(«Kick&Play») предлагает множество возможностей познавать причину и следствие произведенных действий: трогаем висящую игрушку - она раскачивается, стучим по музыкальной панели центра – и можем наблюдать за вспыхивающими огоньками, слушать музыку. Когда ваш малыш подрастет, можно дать ему больше свободы – опустите бортики, арки тоже можно убрать. Теперь наш коврик - полноценная игровая площадка и привычное место для вашего крохи. Для всех малышей важна спокойная и привычная обстановка: идя в гости, возьмите игровой коврик с собой, он очень легкий и компактный. \r\nКоврик мягконабивной, краски безопасные, стойкие. Коврик можно стирать. Игровой комплекс легко собрать и разобрать.', NULL, 0, 0, 1),
(10, 'Развивающий коврик "Заяц"', 3, 1, 'Очень красочный и яркий коврик с дугами и забавными игрушками. Коврик изготовлен из мягчайшей набивной ткани, приятный на ощупь и прекрасно подойдет для первых исследований малыша вне кроватки.\r\nРазличные материалы, яркие цвета, контрастные узоры стимулируют тактильное развитие, развитие слуха, зрения. А навесные игрушки-погремушки-пищалки - это чудо-тренажер для развития моторики. Кроме того, эти игрушки съемные - с ними можно играть и по-отдельности (а вместо них повесить что-то новенькое).\r\nДуги также съемные. Когда малыш подрастет, вы сможете повесить коврик на стене в детской комнате - неплохой вариант декора', NULL, 0, 0, 1),
(11, 'Коляска Grago Mirage', 2, 1, 'Прогулочная коляска Graco Mirage — комфортная, удобная, легкая коляска для малыша.\r\nКоляска Graco Mirage отличается маневренностью благодаря передним вращающимся колесам. Удобное сиденье с жесткой опорой для спинки помогает в развитии правильной осанки ребенка.\r\nХарактеристики Graco Mirage:\r\nЛегкая прогулочная коляска на алюминиевой раме.\r\nВерхние шлейки легко отстёгиваются, и ребёнок может быть пристёгнут только в нижней части сидения.\r\n3 позиции наклона спинки, сидение легко превращается в лежачее место для сна ребёнка и имеет два сидячих положения.\r\nПередние двойные колёса — поворотные, с фиксаторами в переднем направлении.\r\nАмортизаторы передних колес.\r\nСпециальный механизм, установленный в коляске, даёт возможность сложить её одной рукой.\r\nПод сидением расположена объёмная корзина для вещей, доступ к которой не перекрывается, когда сидение разложено полностью.\r\nПодушка сиденья съёмная, пригодна для стирки, легко отстёгивается при помощи кнопок.\r\nЭргономичная неперекидная ручка. Чехол на ножки в комплекте.\r\n', NULL, 0, 0, 1),
(12, 'Happy baby Crossby', 2, 1, 'Прогулочная коляска Happy Baby Crossby. Модель CROSSBY создана в минималистичном стиле, при этом сочетая в себе максимально необходимое количество характеристик для комфортных прогулок и путешествий. Благодаря амортизация на передних и задних колесах, CROSSBY способна передвигаться по любой дороге, включая не глубокий снег.\r\nЭта всесезонная коляска укомплектована чехлом на ножки, дождевиком и москитной сеткой. Имеет широкое сиденье с возможностью плавной регулировки угла наклона спинки, большой капюшон со смотровым окошком, вместительную корзину для покупок.\r\nОбивка коляски выполнена из современных высокотехнологичных тканей, что создает дополнительный комфорт для ребенка и мамы.\r\nОсобенности:\r\nПлавная регулировка наклона спинки, кол-во положений не ограничено \r\nСмотровое окошко на капюшоне\r\nПятиточечные ремни безопасности\r\nСъемный бампер\r\nКолеса: пластиковые с покрытием EVA (этиленвинилацетат)\r\nКолёсная база: передние колеса поворотные с возможностью фиксации\r\nАмортизация передних и задних колес\r\nТормозная педаль на оси задних колес \r\nВместительная корзина для покупок\r\nВ комплекте: дождевик, чехол на ножки, москитная сетка \r\nШирина сиденья: 34 см\r\nГлубина сиденья: 23 см\r\nДлина спального места: 78 см\r\nГабариты в сложенном виде ДхШхВ: 84 х 50.5 х 29 см\r\nГабариты в разложенном виде ДхШхВ: 96 х 50.5 х 104 см\r\nРегулировка наклона спинки: есть\r\nРегулировка ручки: нет\r\nКолеса: пластиковые с покрытием EVA (этиленвинилацетат)\r\nКолёсная база: передние колеса поворотные с возможностью фиксации', NULL, 0, 0, 1),
(13, 'Коляска 2 в 1 X-Lander', 2, 1, '', NULL, 0, 0, 1),
(14, 'Коляска Chicco duo s3', 2, 1, 'Коляска Chicco Duo S3 Black - это универсальная транспортная система для детей от рождения до 3-х лет.\r\nПрогулочная коляска (шасси):\r\n- четыре положения спинки сиденья;\r\n- легкий каркас из алюминиевых труб;\r\n- летний/зимний козырек-капюшон;\r\n- оригинальная конструкция рамы позволяет легко заезжать на бордюры и пр;\r\n- удобная ручка с возможностью регулировки по высоте (95-112 см);\r\n- ручной тормоз и спаренный ножной стояночный тормоз;\r\n- переднее колесо вращается на 360°;\r\n- двухпозиционная система амортизации коляски;\r\n- дополнительный вкладыш с регулируемым по высоте подголовником позволяет чувствовать себя комфортно ребенку любого возраста;\r\n- 5-ти точечный ремень безопасности для ребенка;\r\n- сумка для аксессуаров с прорезиненной пеленкой;\r\n- в комплект входит чехол-дождевик и специальный зимний вкладыш, который с помощью застежки-молнии можно превратить в чехол-накидку для ног;\r\n- ультракомпактная в сложенном виде.\r\nЛюлька:\r\n- перфорированный низ люльки поддерживает оптимальную температуру и влажность;\r\n- передняя часть люльки и новый защитный чехол увеличен (с застежкой по высоте), что обеспечивает дополнительную защиту от ветра и холода;\r\n- в комплект входит специальный матрац для малыша;\r\n- «тихая» система складывания защитного капюшона;\r\n- удобная пластиковая ручка для переноски люльки;\r\n- система "комфорт" — возможность устанавливать наклон спинки регулятором снизу люльки;\r\n- быстрая система установки люльки на шасси колясок S3.\r\nВ комплект коляски входит специальный набор аксессуаров "Trio Car Kit", который предназначен для крепления люльки в автомобиле для детей от рождения и весом до 10 кг (группа 0+): специальное крепление для ремней безопасности и эргономичный пояс, удерживающий малыша в люльке.\r\nРазмеры коляски:\r\n- длина 120 см;\r\n- ширина 59 см;\r\n- высота 95 — 112 см.\r\nРазмер люльки:\r\n- длина 89 см;\r\n- ширина 45 см;\r\n- высота 65 см.\r\nСтрана-производитель: Италия.', NULL, 0, 0, 1),
